                <?php
                    // Query to fetch products based on search query or category ID
                    if (!empty($search_query)) {
                        if ($category_id) {
                            //search and view products the the input names in a specific category by checking whether the search matches any names, and if the C_id matches the category_id
                            $stmt = $conn->prepare("SELECT Product_ID, Product_Name, Product_Image, Product_Price FROM products WHERE Product_Name LIKE ? AND C_ID = ? ORDER BY RAND()");
                            $like_query = "%" . $search_query . "%"; //the % is used for searching for something 'like' the input in the search
                            $stmt->bind_param("si", $like_query, $category_id); //the s means string value, and likequery is the value, i means intiger and categoryid is the integer
                        } else {
                            //search and view products the the input names in any category by checking whether the search matches any names
                            $stmt = $conn->prepare("SELECT Product_ID, Product_Name, Product_Image, Product_Price FROM products WHERE Product_Name LIKE ? ORDER BY RAND()");
                            $like_query = "%" . $search_query . "%"; //the % is used for searching for something 'like' the input in the search
                            $stmt->bind_param("s", $like_query);   //the s means string value, and likequery is the value
                        }
                        $stmt->execute(); //execute the search
                        $result = $stmt->get_result(); //get the results
                    } elseif ($category_id) {
                        //if there is a category but no search then display all products in that category
                        $stmt = $conn->prepare("SELECT Product_ID, Product_Name, Product_Image, Product_Price FROM products WHERE C_ID = ? ORDER BY RAND()");
                        $stmt->bind_param("i", $category_id);   //i means intiger and categoryid is the integer
                        $stmt->execute();
                        $result = $stmt->get_result();
                    } else {
                        //if there is no category and no search then display all products in all categories
                        $result = $conn->query("SELECT Product_ID, Product_Name, Product_Image, Product_Price FROM products ORDER BY RAND()");
                    }

                    //displaying the products in cards each
                    if ($result->num_rows > 0) {
                        while($row = $result->fetch_assoc()) {
                            echo "<div class='col mb-5'>";
                            echo "<div class='card h-100'>";
                            echo "<img class='card-img-top' src='" . $row['Product_Image'] . "' alt='Product Image' />"; //displays the image from the database
                            echo "<div class='card-body p-4'>";
                            echo "<div class='text-center'>";
                            echo "<h5 class='fw-bolder'>" . $row['Product_Name'] . "</h5>"; //display the name from the database
                            echo "<p class='text-muted'>Â£" . number_format($row['Product_Price'], 2) . "</p>"; //display price from the database
                            echo "</div>";
                            echo "</div>";
                            echo "<div class='card-footer p-4 pt-0 border-top-0 bg-transparent'>";
                            echo "<div class='text-center'><a class='btn btn-outline-pink mt-auto' href='http://localhost/GlamLife/GlamLife/product.php?product_id=" . $row['Product_ID'] . "'>View Product</a></div>";
                            echo "</div>";
                            echo "</div>";
                            echo "</div>";
                        }
                    } else {
                        echo "<div class='col-12'><p class='text-center'>No products found.</p></div>"; //if there are no products in  a categpry or at all
                    }

                    $conn->close(); // Close the database connection
                ?>
